import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const libraries = ['places']; // Required for geolocation

const MapComponent = () => {
    const [map, setMap] = useState(null);
    const [currentLocation, setCurrentLocation] = useState(null);
    const [error, setError] = useState(null);
    ap
    useEffect(() => {
        const success = (position) => {
            const { latitude, longitude } = position.coords;
            setCurrentLocation({ lat: latitude, lng: longitude });
        };

        const handleError = (err) => {
            setError(err.message);
        };

        navigator.geolocation.getCurrentPosition(success, handleError);
    }, []);

    const handleLoad = (mapInstance) => {
        setMap(mapInstance);
    };

    const center = currentLocation || { lat: 37.7749, lng: -122.4194 }; 

    return (
        <LoadScript
            googleMapsApiKey="" // Replace with your API key
            libraries={libraries}
            onError={(err) => setError(err)}
        >
            {error ? (
                <div>Error loading Google Maps: {error}</div>
            ) : (
                <GoogleMap
                    mapContainerStyle={{ width: '100vw', height: '400px' }}
                    zoom={currentLocation ? 14 : 10} // Adjust zoom level
                    center={center}
                    onLoad={handleLoad}
                >
                    {currentLocation && (
                        <Marker
                            key="userLocationMarker"
                            position={currentLocation} 
                        />
                    )}
                </GoogleMap>
            )}
        </LoadScript>
    );
};

export default MapComponent;
