import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import redMarkerIcon from './red-marker.svg';
const libraries = ['places']; // Required for geolocation

const MapComponent = () => {
    const [map, setMap] = useState(null);
    const [currentLocation, setCurrentLocation] = useState(null);
    const containerStyle = {
        width: '400px',
        height: '400px',
      };

    const apiKey = 'AIzaSyDl9s9K0XpU0j0i8s5wS7jP0qg7jC7F4p8';
    useEffect(() => {
        const success = (position) => {
            const { latitude, longitude } = position.coords;
            setCurrentLocation({ lat: latitude, lng: longitude });
        };

        const error = () => {
            console.error('Error getting user location');
        };

        navigator.geolocation.getCurrentPosition(success, error);
    }, []);

    const handleLoad = (mapInstance) => {
        setMap(mapInstance);
    };

    const center = currentLocation || { lat: 37.7749, lng: -122.4194 }; 

    return (
        <LoadScript
            googleMapsApiKey={ apiKey } // Replace with your API key
            libraries={libraries}
        >
            <GoogleMap
                mapContainerStyle={{ width: '100vw', height: '400px' }}
                zoom={10}
                center={center}
                onLoad={handleLoad}
            >
                {currentLocation && (
                    <Marker position={currentLocation} 
                    icon={{
                        url: redMarkerIcon,
                        scaledSize: new window.google.maps.Size(30, 30), // Adjust size if needed
                    }}
                    />
                )}
            </GoogleMap>
        </LoadScript>
    );
};


export default MapComponent;
